INSERT INTO content_texts VALUES (2, '<p>700 San Jacinto at 8th Street, Austin, TX<br />Reservations: (512) 476-3700</p><p>There is a wedding block set up at with discounted rates for the weekend. Please call and ask for the \"Ryu/Chen\" wedding block or reserve online with Group Code: 13600109405.</p><p><strong>Must reserve by 8/1.</strong></p><p>Special rate: $139/night for standard guestroom with a King or two Double bed accomodations.</p>');
INSERT INTO content_texts VALUES (3, '<p>We have coordinated buses for guests to take to and from the wedding so everyone can have a great time and not worry about designating drivers.</p><p>Pick-up and drop-off will be from the Omni Austin Downtown Hotel. Please RSVP by 6/27 if you will be taking the shuttle so we can make sure you have a ride!</p><p><b>Pick-up time:</b> 5:30pm<br /><b>Return times:</b> 10:00pm and 11:00pm<br /></p>');
