DROP TABLE IF EXISTS content_links;
CREATE TABLE content_links (id integer PRIMARY KEY, name VARCHAR(255), is_internal bool, url TEXT);

DROP TABLE IF EXISTS content_texts;
CREATE TABLE content_texts (id integer PRIMARY KEY, text text);

DROP TABLE IF EXISTS domains;
CREATE TABLE domains (id integer PRIMARY KEY, wedding_id integer, domain varchar(255), created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS locations;
CREATE TABLE locations (id integer PRIMARY KEY, name varchar(255), address1 varchar(255), address2 varchar(255), city varchar(255), state_id varchar(255), postal_code varchar(255), latitude decimal(9,6), longitude decimal(9,6), created_at timestamp, updated_at timestamp, map_url varchar(255));

DROP TABLE IF EXISTS page_widgets;
CREATE TABLE page_widgets (id integer PRIMARY KEY, page_id integer, widget_type varchar(255), widget_id integer, verticle_order integer, widget_group_id integer);

DROP TABLE IF EXISTS pages;
CREATE TABLE pages (id integer PRIMARY KEY, url_name varchar(255), wedding_id integer, title varchar(255), class_name varchar(255), navigation_order integer, is_locked bool, is_home bool, show_title bool, show_page bool, is_static bool);

DROP TABLE IF EXISTS rsvp_options;
CREATE TABLE rsvp_options (id integer PRIMARY KEY, rsvp_question_id integer, option text);

DROP TABLE IF EXISTS rsvp_questions;
CREATE TABLE rsvp_questions (id integer PRIMARY KEY, wedding_id integer, html_form_type integer, question varchar(255), is_required bool, display_order integer);

DROP TABLE IF EXISTS rsvp_selections;
CREATE TABLE rsvp_selections (id integer PRIMARY KEY, rsvp_id integer, rsvp_question_id integer, rsvp_option_id integer, answer varchar(255));

DROP TABLE IF EXISTS rsvps;
CREATE TABLE rsvps (id integer PRIMARY KEY, wedding_id integer, name varchar(255), can_attend bool, additional_notes text, created_at timestamp, updated_at timestamp);


DROP TABLE IF EXISTS schema_migrations;
CREATE TABLE schema_migrations (version varchar(255), UNIQUE (version));

DROP TABLE IF EXISTS states;
CREATE TABLE states (id integer PRIMARY KEY, name varchar(255), short_name varchar(255), created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS user_auths;
CREATE TABLE user_auths (id integer PRIMARY KEY, user_id integer, service_id integer, login varchar(255), password varchar(255), created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS users;
CREATE TABLE users (id integer PRIMARY KEY, first_name varchar(255), last_name varchar(255), wedding_id integer, role_id integer, facebook_id varchar(255), created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS wedding_authentications;
CREATE TABLE wedding_authentications (id integer PRIMARY KEY, wedding_id integer, service_provider integer, login varchar(255), created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS weddings;
CREATE TABLE weddings (id integer PRIMARY KEY, name varchar(255), name_html varchar(255), rsvp_message varchar(255), date timestamp, text_announcement_image_url varchar(255), photo_gallery_id integer, video_url varchar(255), location_id integer, bride_first_name varchar(255), bride_last_name varchar(255), groom_first_name varchar(255), groom_last_name varchar(255), ga_tag text, created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS widget_albums;
CREATE TABLE widget_albums (id integer PRIMARY KEY, service_provider integer, gallery_id varchar(255), created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS widget_contents;
CREATE TABLE widget_contents (id integer PRIMARY KEY, widget_type varchar(255), widget_id integer, content_type varchar(255), content_id integer, verticle_order integer);

DROP TABLE IF EXISTS widget_events;
CREATE TABLE widget_events (id integer PRIMARY KEY, location_id integer, title varchar(255), start_time timestamp, end_time timestamp, is_rsvp_event bool DEFAULT false, created_at timestamp, updated_at timestamp, description varchar(255));

DROP TABLE IF EXISTS widget_galleries;
CREATE TABLE widget_galleries (id integer PRIMARY KEY, title varchar(255), src text, created_at timestamp, updated_at timestamp);

DROP TABLE IF EXISTS widget_group_widgets;
CREATE TABLE widget_group_widgets (id integer PRIMARY KEY, widget_group_id integer, widget_type varchar(255), widget_id integer);

DROP TABLE IF EXISTS widget_groups;
CREATE TABLE widget_groups (id integer PRIMARY KEY, title varchar(255), description varchar(255), type_id integer);

DROP TABLE IF EXISTS widget_informations;
CREATE TABLE widget_informations (id integer PRIMARY KEY, title varchar(255), title_url text, tab_name varchar(255), description_content_text_id integer);

DROP TABLE IF EXISTS widget_texts;
CREATE TABLE widget_texts (id integer PRIMARY KEY, title varchar(255), text text);
